import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// Contract configuration
const CONTRACT_ID = 'CCNBIWS656R5CR2SJTQMIAMKPDSJS5V4QXLPMC6XIYJQ3GNVJMUHXZW4';
const NETWORK_URL = 'https://soroban-testnet.stellar.org';
const NETWORK_PASSPHRASE = 'Test SDF Network ; September 2015';

// Styled components
const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  min-height: 100vh;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 40px;
  color: white;
`;

const Title = styled.h1`
  font-size: 3rem;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  opacity: 0.9;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 30px;
  margin-bottom: 20px;
  box-shadow: 0 8px 32px rgba(0,0,0,0.1);
  backdrop-filter: blur(10px);
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const Input = styled.input`
  padding: 12px 15px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
  }
`;

const Button = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.4);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Status = styled.div`
  padding: 15px;
  border-radius: 8px;
  margin: 10px 0;
  font-weight: 500;

  &.success {
    background: #d4edda;
    color: #155724;
    border: 1px solid #c3e6cb;
  }

  &.error {
    background: #f8d7da;
    color: #721c24;
    border: 1px solid #f5c6cb;
  }

  &.info {
    background: #d1ecf1;
    color: #0c5460;
    border: 1px solid #bee5eb;
  }
`;

const CampaignCard = styled.div`
  background: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
  border-left: 4px solid #667eea;
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 20px;
  background: #e9ecef;
  border-radius: 10px;
  overflow: hidden;
  margin: 10px 0;
`;

const ProgressFill = styled.div`
  height: 100%;
  background: linear-gradient(90deg, #667eea 0%, #764ba2 100%);
  width: ${props => props.percentage}%;
  transition: width 0.3s ease;
`;

const Label = styled.label`
  font-weight: 600;
  color: #333;
  margin-bottom: 5px;
  display: block;
`;

const Value = styled.div`
  font-size: 1.1rem;
  color: #667eea;
  font-weight: 600;
`;

const ConnectButton = styled(Button)`
  background: linear-gradient(135deg, #28a745 0%, #20c997 100%);
  font-size: 18px;
  padding: 15px 30px;
`;

const WalletInfo = styled.div`
  background: #f8f9fa;
  border-radius: 8px;
  padding: 15px;
  margin: 15px 0;
  border-left: 4px solid #28a745;
`;

function App() {
  const [wallet, setWallet] = useState(null);
  const [connected, setConnected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [campaigns, setCampaigns] = useState([]);

  // Form states
  const [creatorAddress, setCreatorAddress] = useState('');
  const [goal, setGoal] = useState('');
  const [minDonation, setMinDonation] = useState('');
  const [contributionAmount, setContributionAmount] = useState('');
  const [selectedCampaign, setSelectedCampaign] = useState('');

  const connectWallet = async () => {
    try {
      setLoading(true);
      setStatus('Connecting to Stellar wallet...');

      // Check if Freighter is available
      if (typeof window.freighterApi !== 'undefined') {
        try {
          const isConnected = await window.freighterApi.isConnected();
          if (!isConnected) {
            await window.freighterApi.connect();
          }
          const publicKey = await window.freighterApi.getPublicKey();
          setWallet({
            publicKey: publicKey,
            isConnected: true,
            type: 'freighter'
          });
          setConnected(true);
          setStatus('Connected to Freighter wallet successfully!', 'success');
        } catch (error) {
          console.log('Freighter not available or user rejected connection');
        }
      }

      // If Freighter is not available, use demo wallet
      if (!connected) {
        setWallet({
          publicKey: 'GDJTRJM4HWG2D4HRFJDQR3MP5LHM7EDBKW6WIYP4TR6N6GL7KBBFV426',
          isConnected: true,
          type: 'demo'
        });
        setConnected(true);
        setStatus('Using demo wallet for testing. Install Freighter for real transactions!', 'info');
      }

      // Load existing campaigns
      await loadCampaigns();
    } catch (error) {
      setStatus(`Error connecting wallet: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const loadCampaigns = async () => {
    try {
      // For demo, we'll use the test campaign we created
      const testCampaign = {
        address: 'GDJTRJM4HWG2D4HRFJDQR3MP5LHM7EDBKW6WIYP4TR6N6GL7KBBFV426',
        goal: 100000000,
        minDonation: 10000000,
        totalRaised: 20000000,
        supporters: 1
      };
      setCampaigns([testCampaign]);
    } catch (error) {
      console.error('Error loading campaigns:', error);
    }
  };

  const createCampaign = async (e) => {
    e.preventDefault();
    if (!connected) {
      setStatus('Please connect your wallet first', 'error');
      return;
    }

    try {
      setLoading(true);
      setStatus('Creating campaign...');

      // Simulate contract call
      await new Promise(resolve => setTimeout(resolve, 2000));

      const newCampaign = {
        address: creatorAddress,
        goal: parseInt(goal),
        minDonation: parseInt(minDonation),
        totalRaised: 0,
        supporters: 0
      };

      setCampaigns([...campaigns, newCampaign]);
      setStatus('Campaign created successfully!', 'success');

      // Reset form
      setCreatorAddress('');
      setGoal('');
      setMinDonation('');
    } catch (error) {
      setStatus(`Error creating campaign: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const contributeToCampaign = async (e) => {
    e.preventDefault();
    if (!connected) {
      setStatus('Please connect your wallet first', 'error');
      return;
    }

    try {
      setLoading(true);
      setStatus('Processing contribution...');

      // Simulate contract call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Update campaign
      setCampaigns(campaigns.map(campaign => {
        if (campaign.address === selectedCampaign) {
          return {
            ...campaign,
            totalRaised: campaign.totalRaised + parseInt(contributionAmount),
            supporters: campaign.supporters + 1
          };
        }
        return campaign;
      }));

      setStatus('Contribution successful!', 'success');
      setContributionAmount('');
      setSelectedCampaign('');
    } catch (error) {
      setStatus(`Error contributing: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const formatXLM = (stroops) => {
    return (stroops / 10000000).toFixed(2);
  };

  const getProgressPercentage = (raised, goal) => {
    return Math.min((raised / goal) * 100, 100);
  };

  return (
    <Container>
      <Header>
        <Title>🚀 BAF Crowdfunding</Title>
        <Subtitle>Test your Soroban smart contract locally</Subtitle>
      </Header>

      {!connected ? (
        <Card>
          <h2>Connect Your Stellar Wallet</h2>
          <p>Connect your Stellar wallet (like Freighter) to start testing the crowdfunding contract.</p>
          <p><strong>Recommended:</strong> Install <a href="https://www.freighter.app/" target="_blank" rel="noopener noreferrer">Freighter</a> browser extension for the best experience.</p>
          <ConnectButton onClick={connectWallet} disabled={loading}>
            {loading ? 'Connecting...' : 'Connect Wallet'}
          </ConnectButton>
        </Card>
      ) : (
        <>
          <Card>
            <h2>Connected Wallet</h2>
            <WalletInfo>
              <p><strong>Address:</strong> {wallet?.publicKey}</p>
              <p><strong>Type:</strong> {wallet?.type === 'freighter' ? 'Freighter' : 'Demo Wallet'}</p>
              <p><strong>Network:</strong> Testnet</p>
              <p><strong>Contract ID:</strong> {CONTRACT_ID}</p>
            </WalletInfo>
            {wallet?.type === 'demo' && (
              <Status className="info">
                💡 This is a demo wallet. Install Freighter for real transactions!
              </Status>
            )}
          </Card>

          <Grid>
            <Card>
              <h2>Create Campaign</h2>
              <Form onSubmit={createCampaign}>
                <div>
                  <Label>Creator Address:</Label>
                  <Input
                    type="text"
                    value={creatorAddress}
                    onChange={(e) => setCreatorAddress(e.target.value)}
                    placeholder="Enter creator's Stellar address"
                    required
                  />
                </div>
                <div>
                  <Label>Goal (XLM):</Label>
                  <Input
                    type="number"
                    value={goal}
                    onChange={(e) => setGoal(e.target.value)}
                    placeholder="e.g., 100"
                    required
                  />
                </div>
                <div>
                  <Label>Minimum Donation (XLM):</Label>
                  <Input
                    type="number"
                    value={minDonation}
                    onChange={(e) => setMinDonation(e.target.value)}
                    placeholder="e.g., 1"
                    required
                  />
                </div>
                <Button type="submit" disabled={loading}>
                  {loading ? 'Creating...' : 'Create Campaign'}
                </Button>
              </Form>
            </Card>

            <Card>
              <h2>Contribute to Campaign</h2>
              <Form onSubmit={contributeToCampaign}>
                <div>
                  <Label>Select Campaign:</Label>
                  <select
                    value={selectedCampaign}
                    onChange={(e) => setSelectedCampaign(e.target.value)}
                    style={{
                      width: '100%',
                      padding: '12px 15px',
                      border: '2px solid #e1e5e9',
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                    required
                  >
                    <option value="">Choose a campaign...</option>
                    {campaigns.map((campaign, index) => (
                      <option key={index} value={campaign.address}>
                        {campaign.address.slice(0, 8)}... - Goal: {formatXLM(campaign.goal)} XLM
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <Label>Amount (XLM):</Label>
                  <Input
                    type="number"
                    value={contributionAmount}
                    onChange={(e) => setContributionAmount(e.target.value)}
                    placeholder="e.g., 5"
                    required
                  />
                </div>
                <Button type="submit" disabled={loading}>
                  {loading ? 'Contributing...' : 'Contribute'}
                </Button>
              </Form>
            </Card>
          </Grid>

          <Card>
            <h2>Active Campaigns</h2>
            {campaigns.length === 0 ? (
              <p>No campaigns found. Create one to get started!</p>
            ) : (
              <div style={{ display: 'grid', gap: '20px' }}>
                {campaigns.map((campaign, index) => (
                  <CampaignCard key={index}>
                    <h3>Campaign {index + 1}</h3>
                    <p><strong>Creator:</strong> {campaign.address}</p>
                    <p><strong>Goal:</strong> {formatXLM(campaign.goal)} XLM</p>
                    <p><strong>Raised:</strong> {formatXLM(campaign.totalRaised)} XLM</p>
                    <p><strong>Supporters:</strong> {campaign.supporters}</p>
                    <p><strong>Min Donation:</strong> {formatXLM(campaign.minDonation)} XLM</p>

                    <div style={{ marginTop: '15px' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
                        <span>Progress</span>
                        <span>{getProgressPercentage(campaign.totalRaised, campaign.goal).toFixed(1)}%</span>
                      </div>
                      <ProgressBar>
                        <ProgressFill percentage={getProgressPercentage(campaign.totalRaised, campaign.goal)} />
                      </ProgressBar>
                    </div>

                    {campaign.totalRaised >= campaign.goal && (
                      <Status className="success" style={{ marginTop: '10px' }}>
                        🎉 Goal reached! Creator can withdraw funds.
                      </Status>
                    )}
                  </CampaignCard>
                ))}
              </div>
            )}
          </Card>
        </>
      )}

      {status && (
        <Status className={status.includes('Error') ? 'error' : status.includes('successfully') ? 'success' : 'info'}>
          {status}
        </Status>
      )}
    </Container>
  );
}

export default App;
